
public class TestVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Hola nuevo test");    

        int edad = 37;

        System.out.println("La edad es "+ edad + ", Felicitaciones!");
        
        edad = 37+10;

        System.out.println("La edad es "+ edad + ", Felicitaciones!");
        
        edad = 37-10;

        System.out.println("La edad es "+ edad + ", Felicitaciones!");
        
        edad = 37*2;

        System.out.println("La edad es "+ edad + ", Felicitaciones!");
        
        edad = 37/4;

        System.out.println("La edad es "+ edad + ", Felicitaciones!");
	}

}
