
public class TipoVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Cast de int
		double variable1 = 230.89;
		int variableEntero = (int) variable1; //Cast
		System.out.println(variableEntero);
		
		long prueba = 2999999999999999999L;
		System.out.println(prueba);
		short numeroPequeno = 1355;
		System.out.println(numeroPequeno);
		byte numeroAunMasPequeno = 15;
		System.out.println(numeroAunMasPequeno);
		float numeroDecimalPequeno = 2.5F;
		System.out.println(numeroDecimalPequeno);
		
		int resultado = (int) variable1 + variableEntero;
		System.out.println(resultado);
		
		
	}

}
